} //in WEdaf09627af..OEDynTag0.html1

function OEFICode1($p) { if (!is_array($p)) return '';
	// $p[0]=curr page (first page = 1), ($p[1] not used), $p[2]=self-link, $p[3]=total pages
if ($p[3] < 2) return '1/1';

$selfLink = "{$p[2]}?{$p[4]}&";

$maxPagesShowAll = 10;
$near_startend = 2;
$near_current = 2;

$r = '';
$r .= ($p[0]>1) ? ("<a href=\"{$selfLink}oepage1=1\">&lt;&lt;</a>") : '|&lt;&lt;';
$r .= '&nbsp;&nbsp;';
$r .= ($p[0]>1) ? ("<a href=\"{$selfLink}oepage1=".($p[0]-1).'">&lt;</a>') : '&lt;';
$r .= '&nbsp;&nbsp;';

$sp = '';

for ($i=1; $i<=$p[3]; $i++) {
	if ($p[3] > $maxPagesShowAll) { // too many pages to show at once
		if (!($i >= $p[0]-$near_current && $i <= $p[0]+$near_current) && 
		    !($i <= $near_startend || $i >= $p[3]+1-$near_startend)) { 
                        // not near current page or start or end
			if ($i == $near_startend+1 || $i == $p[3]-$near_startend) $sp .= ' .. '; 
                        // ex "1 2 .." and ".. 41 42"
			continue; // don't show page number
		} 
	}
	// show page number, with link if not current page
	
	$pageLink = $selfLink.'oepage1='.$i; // ex "admin.php?oepage=4"
	$pageSt   = ($i == $p[0]) ? '<b>'.$i.'</b>' : "<a href='$pageLink'>$i</a>";
	$sp .= '&nbsp;'.$pageSt.'&nbsp;';
}
$r .= $sp;

$r .= '&nbsp;&nbsp;';
$r .= ($p[0]<$p[3]) ? ("<a href=\"{$selfLink}oepage1=".($p[0]+1).'">&gt;</a>') : '&gt;';
$r .= '&nbsp;&nbsp;';
$r .= ($p[0]<$p[3]) ? ("<a href=\"{$selfLink}oepage1={$p[3]}\">&gt;&gt;</a>") : '&gt;&gt;|';

return $r;
} //in WE9ad3d82879..OEDynTag0.html1

function OEFICode2($p) { if (!is_array($p)) return '';
	return ($p[0] > 0 && $p[1] > 0) ? ceil($p[0]/$p[1]) : 1;
} //in PK1!FI_Pages_TotalPages

function OEFICode3($p) { if (!is_array($p)) return '';
	// ex. 2000/01/16 if hous and minutes = 0, or 2000/01/16 16:40 otherwise
	if (!$p[1]) return "";
	$date=date("Y/m/d", $p[1]);
	$time=date("G:i", $p[1]);$time=($time==="0:00")?"":" $time";
	return $date.$time;
} //in PK1!FI_DateOfficial_TStoText

function OEFICode4($p) { if (!is_array($p)) return '';
	return (!$p[0] || !$p[1]) ? 0 : ($p[0]-1)*$p[1];
} //in PK1!FI_Pages_CurrOffset

function OEFICode5($p) { if (!is_array($p)) return '';
	return $p[0]*24*60;
} //in PK1!FI_PW_Cookie_Duration_Min

function OEFICode6($p) { if (!is_array($p)) return '';
	// get Unix int timestamp from string datetime ex. "2000/01/16 4:08" -> 947992080  
	if (!$p[0]) return 0;
	$r = strtotime($p[0], 0); if (!$r || $r===-1) return 0;
	return $r;
} //in PK1!FI_DateOfficial_TextToTS

function OEFICode7($p) { if (!is_array($p)) return '';
	$r = '';
	$total = ($p[0]) ? $p[0] : 0;
	$current = ($p[1]) ? $p[1] : -1;
	
	for ($i=1; $i<=$total; $i++) {
		$value = $i; // page number
		$name = $i;
		$selected = (intval($i) === intval($current)) ? " selected='selected'" : ' ';
		$r .= "<option value='$value'$selected>$name</option>\r\n";
	}
	return $r;
} //in WEd854ba94da..OEDynTag0.html1

